fn get_final_floor(instruction: &str) -> isize {
    let instruction = instruction.as_bytes();

    let mut open_parens = 0;
    let mut close_parens = 0;

    for character in instruction.iter() {
        match character {
            b'(' => open_parens+= 1,
            b')' => close_parens+= 1,
            _ => (),
        }
    }

    println!("Number of open parens: {}\nNumber of close parens: {}", open_parens, close_parens);

    open_parens - close_parens
}

fn find_position_of_floor(desired_floor: i32, instruction: &str) -> usize {
    let instruction = instruction.as_bytes();

    let mut current_floor: i32 = 0;

    for (position, character) in instruction.iter().enumerate() {
        match character {
            b'(' => current_floor+= 1,
            b')' => current_floor-= 1,
            _ => (),
        }

        if current_floor == desired_floor { return position + 1; }
    }

    0
}